  }
`;

const baseStyle = css`
  position: relative;
  // Establishes the root element as a new stacking context
  // so that the z-index of the span within the button doesn't
  // appear above other elements on the page that it shouldn't.
  z-index: 0;
  border-radius: 3px;
  box-sizing: border-box;
  cursor: pointer;
  display: inline-flex;
  align-items: stretch;
  font-family: Akzidenz, 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-weight: normal;
  text-decoration: none;
  text-transform: none;
  transition: all 120ms ease;
  user-select: none;
  overflow: hidden;
  padding: 0;

  &:hover {
    text-decoration: none;
  }

  // We're using CSS pseudo elements here in order to
  // transition the gradients between button pseudo classes.
  &:before,
  &:after {
    content: '';
    transition: 0.15s opacity ease-in-out;
    opacity: 0;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
  }

  &:not(:disabled) {
    &:focus,
    &:hover {
      &:before {
        opacity: 1;
      }
    }

    &:active:after {
      opacity: 1;
    }
  }
`;

const disabledStyle = css`
  color: ${uiColors.gray.base};
  border-color: ${uiColors.gray.light1};
  background-color: ${uiColors.gray.light2};
  background-image: none;
  box-shadow: none;
  pointer-events: none;
`;

interface BaseButtonProps {
  disabled?: boolean;
  variant?: Variant;
  size?: Size;
  glyph?: React.ReactElement;
  className?: string;
  children?: React.ReactNode;
  href?: string;
}

const Button: ExtendableBox<BaseButtonProps, 'button'> = React.forwardRef(
  (
    {
      className,
      children,
      disabled = false,
      variant = Variant.Default,
      size = Size.Normal,
      glyph,
      ...rest
    }: BaseButtonProps,
    ref: React.Ref<any>,
  ) => {
    const { usingKeyboard: showFocus } = useUsingKeyboardContext();

    const commonProps = {
      ref,
      className: cx(
        baseStyle,
        buttonSizes[size],
        buttonVariants[variant],
        { [disabledStyle]: disabled },
        { [focusStyle]: showFocus },
        className,
      ),
      // only add a disabled prop if not an anchor
      ...(typeof rest.href !== 'string' && { disabled }),
      'aria-disabled': disabled,
    };

    const spanStyle = css`
      // Usually for this to take effect, you would need the element to be
      /* ￿positioned￿. Due to an obscure part of CSS spec, flex children */
      // respect z-index without the position property being set.
      //
      // https://www.w3.org/TR/css-flexbox-1/#painting
      z-index: 1;
      display: inline-flex;
      flex-grow: 1;
      justify-content: center;
      align-items: center;
    `;

    const modifiedGlyph =
      glyph && children
        ? React.cloneElement(glyph, {
            className: cx({ [glyphMargins[size]]: glyph != null }),
          })
        : glyph;

    const content = (
      <span className={cx(spanStyle, spanSizes[size])}>
        {modifiedGlyph}
        {children}
      </span>
    );

    if (typeof rest.href === 'string') {
      return (
        <Box as="a" {...commonProps} {...rest}>
          {content}
        </Box>
      );
    }

    return (
      // we give button a default "as" value based on the `href` prop, if a custom
      // "as" prop is supplied, it will overwrite this value through {...rest}
      <Box as="button" type="button" {...commonProps} {...rest}>
        {content}
      </Box>
    );
  },
);

// @ts-expect-error: Property 'displayName' does not exist on type 'OverrideComponentCast<BaseButtonProps>'.ts(2339)
Button.displayName = 'Button';

// @ts-expect-error: https://github.com/DefinitelyTyped/DefinitelyTyped/issues/37660
Button.propTypes = {
  variant: PropTypes.oneOf(Object.values(Variant)),
  size: PropTypes.oneOf(Object.values(Size)),
  className: PropTypes.string,
  children: PropTypes.node,
  disabled: PropTypes.bool,
  // @ts-ignore
  as: PropTypes.oneOfType([
    PropTypes.element,
    PropTypes.elementType,
    PropTypes.func,
  ]),
  href: PropTypes.string,
  glyph: PropTypes.element,
};

export default Button;
